import React, { useState, useCallback, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import { Firebase, firedb } from "../firebase";
import { AuthContext } from "../auth/AuthContext";
import { Form, Button, Container, Row, Col, ButtonGroup, ToggleButton } from 'react-bootstrap'


const EditReservations = (props) => {
    var [name, setName] = useState(props.name)
    const [email, setEmail] = useState(props.email)
    const [date, setDate] = useState(props.date)
    const [hotel, setHotel] = useState(props.hotel)
    const [numberofpeople, setNumberofpeople] = useState(props.numberofpeople)
    const [time, setTime] = useState(props.time)



    const handleEdit = () => {
        const details = {
            name: name,
            email: email,
            Date: date,
            Time: time,
            numberofpeople: numberofpeople,
            Hotel: hotel
        };
        console.log(details)

        try {
            firedb
                .collection("reservations")
                .where("id", "===", `${props.id}`)
                .set(                                    //updates the values for the selected id from the table in the firestore
                    details,
                    err => {
                        if (err) {
                            console.log(err)
                        }

                    }
                )

        } catch (error) {
            alert(error);
        }
    }



    return (
        <div style={{ width: "50%", marginLeft: "25%"}}>
            <h1>Edit your reservation here</h1>

            <Form.Group controlId="formBasicName" style={{ marginTop: "13%" }}>
                <Form.Label style={{ fontSize: "25px" }}>Name</Form.Label>
                <Form.Control type="text" placeholder="Enter name" value={props.name} onChange={(event) => {
                    setName(event.target.value)
                }} />

            </Form.Group>
            <Form.Group controlId="formBasicNumber">
                <Form.Label style={{ fontSize: "25px" }}>Email</Form.Label>
                <Form.Control type="email" placeholder="Enter email" value={props.email} onChange={(event) => {
                    setEmail(event.target.value)
                }} />

            </Form.Group>
            <Form.Group controlId="formBasicNumber">
                <Form.Label style={{ fontSize: "25px" }}>Number of people</Form.Label>
                <Form.Control type="number" placeholder="Enter number of people" value={props.numberofpeople} onChange={(event) => {
                    setNumberofpeople(event.target.value)
                }} />

            </Form.Group>
            <Form.Group controlId="formBasicEmail">
                <Form.Label style={{ fontSize: "25px" }}>Hotel</Form.Label>
                <Form.Control type="text" placeholder="Enter hotel" value={props.hotel} onChange={(event) => {
                    setHotel(event.target.value)
                }} />

            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label style={{ fontSize: "25px" }}>Date</Form.Label>
                <Form.Control type="text" placeholder="Enter date" value={props.date} onChange={(event) => {
                    setDate(event.target.value)
                }} />
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
                <Form.Label style={{ fontSize: "25px" }}>Time</Form.Label>
                <Form.Control type="text" placeholder="Enter time" value={props.time} onChange={(event) => {
                    setTime(event.target.value)
                }} />
            </Form.Group>

            <Button variant="primary" type="submit" size="lg" onClick={handleEdit} block style={{ backgroundColor: "#F72A1F"}}>
                Edit
            </Button>
        </div>
    );
};

export default EditReservations;
