  },
  heading: {
    paddingTop: theme.spacing(3),
    // paddingBottom: theme.spacing(5),
  },
  buttons: {
    width: '400px',
  },
  backButton: {
    marginTop: theme.spacing(3),
  },
  submitButton: {
    marginTop: theme.spacing(3),
  },
  textFields: {
    marginBottom: theme.spacing(3),
  },
  icons: {
    color: '#fff',
  },
}));

export const LoginForm = () => {
  const classes = useStyles();
  const [settings, testSettings] = useContext(SettingsContext);
  const [management, setManagement] = useState(false)
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    password2: '',
    loggedin: false,
    username: '',
    errors: {},
  });

  const { email, password, errors } = formData;

  console.log(settings)

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmitManagement = (e) => {
    e.preventDefault();

    let data = {
      usrEmail: email,
      usrPassword: password,
    };

    console.log(data);
    try {
      Firebase
        .auth()
        .signInWithEmailAndPassword(email, password)
        .then(response => {

          console.log(response)
          console.log("ook")
          // history.push("/home");
          // return (<Redirect to="/home" />)
            window.location.href = "/management"

        })
      // history.push("/home");
    } catch (error) {
      alert(error);
    }
  };

  const onSubmitAdmin = (e) => {
    e.preventDefault();

    let data = {
      usrEmail: email,
      usrPassword: password,
    };

    console.log(data);
    try {
      Firebase
        .auth()
        .signInWithEmailAndPassword(email, password)
        .then(response => {

          console.log(response)
          console.log("ook")
          // history.push("/home");
          // return (<Redirect to="/home" />)
            window.location.href = "/admin"

        })
      // history.push("/home");
    } catch (error) {
      alert(error);
    }
  };

  const panes = [
    {
      menuItem: 'Management', render: () => <Tab.Pane>
        <div>

          <Typography
            variant='h5'
            align='center'
            className={classes.heading}
            style={{ marginBottom: "5%" }}
          >
            <Box>Management Login</Box>
          </Typography>
          <Grid container justify='center'>
            <Grid item>
              <div className={classes.form}>
                <form onSubmit={(e) => onSubmitManagement(e)}>
                  <TextField
                    id='email'
                    type='email'
                    label='Enter Managment Email Address'
                    name='email'
                    value={email}
                    onChange={(e) => onChange(e)}
                    // error={errors.email}
                    // helperText={errors.email}
                    variant='outlined'
                    fullWidth
                    className={classes.textFields}
                    error={
                      !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                        email
                      ) && email !== ""
                    }
                    helperText={
                      !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                        email
                      ) && email !== ""
                        ? "Invalid email"
                        : ""
                    }
                  />
                  <TextField
                    id='password'
                    type='password'
                    name='password'
                    label='Enter Password'
                    value={password}
                    onChange={(e) => onChange(e)}
                    // error={errors.phone}
                    // helperText={errors.phone}
                    variant='outlined'
                    fullWidth
                    className={classes.textFields}
                  />

                  <Typography align='right'>
                    <Box fontWeight={900}>Forgot Password?</Box>
                  </Typography>

                  <Button
                    variant='contained'
                    color='primary'
                    className={classes.submitButton}
                    fullWidth
                    type='submit'
                  >
                    <Box fontSize={16}>Log In</Box>
                  </Button>


                </form>
              </div>
            </Grid>
          </Grid>
        </div>
      </Tab.Pane>
    },
    {
      menuItem: 'Admin', render: () => <Tab.Pane>
        <Typography
          variant='h5'
          align='center'
          className={classes.heading}
          style={{ marginBottom: "5%" }}
        >
          <Box>Admin Login</Box>
        </Typography>
        <Grid container justify='center'>
          <Grid item>
            <div className={classes.form}>
              <form onSubmit={(e) => onSubmitAdmin(e)}>
                <TextField
                  id='email'
                  type='email'
                  label='Enter Admin Email Address'
                  name='email'
                  value={email}
                  onChange={(e) => onChange(e)}
                  // error={errors.email}
                  // helperText={errors.email}
                  variant='outlined'
                  fullWidth
                  className={classes.textFields}
                  error={
                    !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                      email
                    ) && email !== ""
                  }
                  helperText={
                    !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                      email
                    ) && email !== ""
                      ? "Invalid email"
                      : ""
                  }
                />
                <TextField
                  id='password'
                  type='password'
                  name='password'
                  label='Enter Password'
                  value={password}
                  onChange={(e) => onChange(e)}
                  // error={errors.phone}
                  // helperText={errors.phone}
                  variant='outlined'
                  fullWidth
                  className={classes.textFields}
                />

                <Typography align='right'>
                  <Box fontWeight={900}>Forgot Password?</Box>
                </Typography>

                <Button
                  variant='contained'
                  color='primary'
                  className={classes.submitButton}
                  fullWidth
                  type='submit'
                >
                  <Box fontSize={16}>Log In</Box>
                </Button>
              </form>
            </div>
          </Grid>
        </Grid>
      </Tab.Pane>
    },
  ]



  if (formData.loggedin === true) {
    return <Redirect
      to={{
        pathname: '/admin/dashboard',
        state: { username: formData.username }
      }}
    />
    // <Admindashboard name={formData.username}/>
  } else {
    return (
      <div>
        <Grid container component='main' className={classes.root}>
          <CssBaseline />
          <Hidden smDown>
            <Grid
              md={8}
            >
              <img src={Reception} alt='overlay' style={{ maxHeight: "100%", maxWidth: "100%", overflow: "hidden"}} />
            </Grid>
          </Hidden>
          <Grid item xs={12} sm={12} md={4} component={Paper} elevation={6} >
            <div className={classes.paper}>
              <img src={FaceGenieLogo} alt='FaceGenieLogo' />
              <Typography variant='h5'>
                <Box>
                  Attendance Management System
              </Box>
              </Typography>
              <div className={classes.buttons}>
                <Fragment>
                  <Link to='/'>
                    <Button
                      startIcon={<img src={BackIcon} />}
                      className={classes.backButton}
                    >
                      Go Back
                  </Button>
                  </Link>
                  <Typography
                    variant='h5'
                    align='center'
                    className={classes.heading}
                    style={{ marginBottom: "3%" }}
                  >
                    <Box fontWeight={800}>Enter your details to Log In</Box>
                  </Typography>
                  <Container maxWidth='xl'>


                    {settings.MultiUserManagement ?
                      <Tab panes={panes} />

                      :

                      <div>
                        <Typography
                          variant='h5'
                          align='center'
                          className={classes.heading}
                          style={{ marginBottom: "5%" }}
                        >
                          <Box>Admin Login</Box>
                        </Typography>
                        <Grid container justify='center'>
                          <Grid item>
                            <div className={classes.form}>
                              <form onSubmit={(e) => onSubmitAdmin(e)}>
                                <TextField
                                  id='email'
                                  type='email'
                                  label='Enter Admin Email Address'
                                  name='email'
                                  value={email}
                                  onChange={(e) => onChange(e)}
                                  // error={errors.email}
                                  // helperText={errors.email}
                                  variant='outlined'
                                  fullWidth
                                  className={classes.textFields}
                                  error={
                                    !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                      email
                                    ) && email !== ""
                                  }
                                  helperText={
                                    !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                      email
                                    ) && email !== ""
                                      ? "Invalid email"
                                      : ""
                                  }
                                />
                                <TextField
                                  id='password'
                                  type='password'
                                  name='password'
                                  label='Enter Password'
                                  value={password}
                                  onChange={(e) => onChange(e)}
                                  // error={errors.phone}
                                  // helperText={errors.phone}
                                  variant='outlined'
                                  fullWidth
                                  className={classes.textFields}
                                />

                                <Typography align='right'>
                                  <Box fontWeight={900}>Forgot Password?</Box>
                                </Typography>

                                <Button
                                  variant='contained'
                                  color='primary'
                                  className={classes.submitButton}
                                  fullWidth
                                  type='submit'
                                >
                                  <Box fontSize={16}>Log In</Box>
                                </Button>
                              </form>
                            </div>
                          </Grid>
                        </Grid>
                      </div>
                    }




                  </Container>
                  <Typography style={{ marginTop: '4%', marginLeft: '10%' }}>
                    <Box>
                      Don't have an account?{' '}
                      <Link to='/signup' style={{ color: 'red' }}>
                        Click here
                    </Link>{' '}
                    to Sign Up{' '}
                    </Box>
                  </Typography>
                </Fragment>
              </div>
            </div>
          </Grid>
        </Grid>
      </div>

    );
  }
};
