import React, { Fragment, useState, useContext } from 'react';
// import { Admindashboard } from '../Components/Admindashboard';

// Package Imports
import { Link, Redirect } from 'react-router-dom';
import axios from 'axios';

// Icon & Placeholders Imports
import Reception from '../placeholders/reception.jpg'
import BackIcon from '../placeholders/BackIcon.png';

// Material-UI Imports
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Hidden from '@material-ui/core/Hidden';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import { Firebase, firedb } from '../firebase'

import { Tab } from 'semantic-ui-react'

// Styling Landing Page
const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
    },
    paper: {
        marginTop: theme.spacing(15),
        [theme.breakpoints.down('md')]: {
            marginTop: theme.spacing(10),
        },
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    heading: {
        paddingTop: theme.spacing(3),
        // paddingBottom: theme.spacing(5),
    },
    buttons: {
        width: '400px',
    },
    backButton: {
        marginTop: theme.spacing(3),
    },
    submitButton: {
        marginTop: theme.spacing(3),
    },
    textFields: {
        marginBottom: theme.spacing(3),
    },
    icons: {
        color: '#fff',
    },
}));

export const Landing = () => {
    const classes = useStyles();
    const [formData, setFormData] = useState({
        username: '',
        contactNumber: '',
        email: '',
        password: '',
        password2: '',
        loggedin: false,
        username: '',
        errors: {},
    });

    const { email, password, errors } = formData;


    const onChange = (e) =>
        setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmitManagement = (e) => {
        e.preventDefault();

        let data = {
            usrEmail: email,
            usrPassword: password,
        };

        console.log(data);
        try {
            Firebase
                .auth()
                .signInWithEmailAndPassword(email, password)
                .then(response => {

                    console.log(response)
                    console.log("ook")
                    // history.push("/home");
                    // return (<Redirect to="/home" />)
                    window.location.href = "/management"

                })
            // history.push("/home");
        } catch (error) {
            alert(error);
        }
    };

    const onSubmit = (e) => {
        e.preventDefault();

        let data = {
            usrEmail: formData.email,
            usrPassword: formData.password,
        };

        console.log(data);
        try {
            Firebase
                .auth()
                .signInWithEmailAndPassword(formData.email, formData.password)
                .then(response => {

                    console.log(response)
                    console.log("ook")
                    // history.push("/home");
                    // return (<Redirect to="/home" />)
                    window.location.href = "/admin"

                })
            // history.push("/home");
        } catch (error) {
            alert(error);
        }
    };

    const signup = (e) => {
        e.preventDefault()
        const details = {
            email: formData.email,
            username: formData.username,
            password1: formData.password,
            password2: formData.password2,
            ph_number: formData.contactNumber,
        };
        console.log(JSON.stringify(details));
        Firebase
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then((user) => {
                if (user) {
                    console.log(JSON.stringify(user));
                    firedb.collection("users")
                        .add(details)
                        .then((doc) => {
                            console.log(doc.id);
                            alert("Account has been created! Please login with your cedentials")
                        })
                        .catch((err) =>
                            console.log("error adding user to firebase: ", err)
                        );
                }
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const panes = [
        {
            menuItem: 'Sign-In', render: () => <Tab.Pane>
                <div>

                    <Typography
                        variant='h5'
                        align='center'
                        className={classes.heading}
                        style={{ marginBottom: "5%" }}
                    >
                        <Box>Enter your credentials</Box>
                    </Typography>
                    <Grid container justify='center'>
                        <Grid item>
                            <div className={classes.form}>
                                <form onSubmit={(e) => onSubmit(e)}>
                                    <TextField
                                        id='email'
                                        type='email'
                                        label='Enter Email Address'
                                        name='email'
                                        value={email}
                                        onChange={(e) => onChange(e)}
                                        // error={errors.email}
                                        // helperText={errors.email}
                                        variant='outlined'
                                        fullWidth
                                        className={classes.textFields}
                                        error={
                                            !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                                email
                                            ) && email !== ""
                                        }
                                        helperText={
                                            !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                                email
                                            ) && email !== ""
                                                ? "Invalid email"
                                                : ""
                                        }
                                    />
                                    <TextField
                                        id='password'
                                        type='password'
                                        name='password'
                                        label='Enter Password'
                                        value={password}
                                        onChange={(e) => onChange(e)}
                                        // error={errors.phone}
                                        // helperText={errors.phone}
                                        variant='outlined'
                                        fullWidth
                                        className={classes.textFields}
                                    />

                                    <Typography align='right'>
                                        <Box fontWeight={900}>Forgot Password?</Box>
                                    </Typography>

                                    <Button
                                        variant='contained'
                                        color='primary'
                                        className={classes.submitButton}
                                        fullWidth
                                        type='submit'
                                    >
                                        <Box fontSize={16}>Log In</Box>
                                    </Button>


                                </form>
                            </div>
                        </Grid>
                    </Grid>
                </div>
            </Tab.Pane>
        },
        {
            menuItem: 'Sign-Up', render: () => <Tab.Pane>
                <Typography
                    variant='h5'
                    align='center'
                    className={classes.heading}
                    style={{ marginBottom: "5%" }}
                >
                    <Box>Sign-Up</Box>
                </Typography>
                <Grid container justify='center'>
                    <Grid item>
                        <div className={classes.form}>
                            <form onSubmit={(e) => signup(e)}>
                                <TextField
                                    id='name'
                                    type='name'
                                    label='Enter Name'
                                    name='username'
                                    value={formData.username}
                                    onChange={(e) => onChange(e)}
                                    // error={errors.email}
                                    // helperText={errors.email}
                                    variant='outlined'
                                    fullWidth
                                    className={classes.textFields}

                                />
                                 <TextField
                                    id='number'
                                    type='number'
                                    label='Enter Phone Number'
                                    name='contactNumber'
                                    value={formData.contactNumber}
                                    onChange={(e) => onChange(e)}
                                    // error={errors.email}
                                    // helperText={errors.email}
                                    variant='outlined'
                                    fullWidth
                                    className={classes.textFields}

                                />
                                <TextField
                                    id='email'
                                    type='email'
                                    label='Enter Email Address'
                                    name='email'
                                    value={email}
                                    onChange={(e) => onChange(e)}
                                    // error={errors.email}
                                    // helperText={errors.email}
                                    variant='outlined'
                                    fullWidth
                                    className={classes.textFields}
                                    error={
                                        !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                            email
                                        ) && email !== ""
                                    }
                                    helperText={
                                        !/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
                                            email
                                        ) && email !== ""
                                            ? "Invalid email"
                                            : ""
                                    }
                                />
                                <TextField
                                    id='password'
                                    type='password'
                                    name='password'
                                    label='Enter Password'
                                    value={password}
                                    onChange={(e) => onChange(e)}
                                    // error={errors.phone}
                                    // helperText={errors.phone}
                                    variant='outlined'
                                    fullWidth
                                    className={classes.textFields}
                                />
                                <TextField
                                    id='password2'
                                    type='password2'
                                    name='password2'
                                    label='Re-Enter Password'
                                    value={formData.password2}
                                    onChange={(e) => onChange(e)}
                                    // error={errors.phone}
                                    // helperText={errors.phone}
                                    variant='outlined'
                                    fullWidth
                                    className={classes.textFields}
                                    error={formData.password2 !== formData.password && formData.password2 !== ""}
                                    helperText={
                                        formData.password2 !== formData.password && formData.password2 !== ""
                                            ? "Passwords do not match"
                                            : ""
                                    }
                                />
                                <Typography align='right'>
                                    <Box fontWeight={900}>Forgot Password?</Box>
                                </Typography>

                                <Button
                                    variant='contained'
                                    color='primary'
                                    className={classes.submitButton}
                                    fullWidth
                                    type='submit'
                                >
                                    <Box fontSize={16}>Submit</Box>
                                </Button>
                            </form>
                        </div>
                    </Grid>
                </Grid>
            </Tab.Pane>
        },
    ]



    if (formData.loggedin === true) {
        return <Redirect
            to={{
                pathname: '/admin/dashboard',
                state: { username: formData.username }
            }}
        />
        // <Admindashboard name={formData.username}/>
    } else {
        return (
            <div>
                <Grid container component='main' className={classes.root}>
                    <CssBaseline />
                    <Hidden smDown>
                        <Grid
                            md={8}
                        >
                            <img src={Reception} alt='overlay' style={{ maxHeight: "120%", maxWidth: "115%", overflow: "hidden" }} />
                        </Grid>
                    </Hidden>
                    <Grid item xs={12} sm={12} md={4} component={Paper} elevation={6} >
                        <div className={classes.paper}>
                            <Typography variant='h3'>
                                <Box fontWeight={800}>
                                    Online Hotel Reservation
              </Box>
                            </Typography>
                            <div className={classes.buttons}>
                                <Fragment>
                                    <Typography
                                        variant='h5'
                                        align='center'
                                        className={classes.heading}
                                        style={{ marginBottom: "3%" }}
                                    >
                                        <Box >Enter your details to Log In</Box>
                                    </Typography>
                                    <Container maxWidth='xl'>


                                        <Tab panes={panes} />






                                    </Container>
                                    <Typography style={{ marginTop: '4%', marginLeft: '10%' }}>
                                        <Box>
                                            Don't have an account?{' '}
                                            <Link to='/signup' style={{ color: 'red' }}>
                                                Click here
                    </Link>{' '}
                    to Sign Up{' '}
                                        </Box>
                                    </Typography>
                                </Fragment>
                            </div>
                        </div>
                    </Grid>
                </Grid>
            </div>

        );
    }
};
