//main page after login
import React, { useContext, useEffect, useState } from 'react';
import { Firebase, firedb } from '../firebase'
import { AuthContext } from '../auth/AuthContext'
import { Redirect } from 'react-router-dom';

//material-ui imports
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { Divider } from "@material-ui/core";


const useStyles = makeStyles((theme) => ({
    icon: {
        marginRight: theme.spacing(2),
    },
    heroContent: {
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(8, 0, 6),
    },
    heroButtons: {
        marginTop: theme.spacing(4),
    },
    cardGrid: {
        paddingTop: theme.spacing(8),
        paddingBottom: theme.spacing(8),
    },
    card: {
        height: "100%",
        display: "flex",
        flexDirection: "column",
    },
    cardMedia: {
        paddingTop: "56.25%", // 16:9
    },
    cardContent: {
        flexGrow: 1,
    },
    footer: {
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(6),
    },
    redDot: {
        height: 20,
        width: 20,
        backgroundColor: "red",
        borderRadius: "50%",
        display: "inline-block",
    },
    yellowDot: {
        height: 20,
        width: 20,
        backgroundColor: "yellow",
        borderRadius: "50%",
        display: "inline-block",
    },
    greenDot: {
        height: 20,
        width: 20,
        backgroundColor: "green",
        borderRadius: "50%",
        display: "inline-block",
    },
}));

var temp = [];

function Home(props) {
    const classes = useStyles();
    const admin = useContext(AuthContext);
    const [username, setUsername] = useState('')
    const [phone, setPhone] = useState('')
    const [email, setEmail] = useState('')
    const [reservations, setReservations] = useState([])

    // console.log(admin.admin.email)
    // console.log(admin.admin.providerData)
    // console.log(admin.admin.uid)

    useEffect(() => {
        firedb
            .collection("users")
            .where("email", '==', `${admin.admin.email}`)
            .get()
            .then(querySnapshot => {
                querySnapshot.forEach(doc => {
                    // console.log(doc.data())
                    setUsername(doc.data().username)
                    setPhone(doc.data().ph_number)
                    setEmail(doc.data().email)
                })
            })

        firedb
            .collection("reservations")
            .where("email", '==', `${admin.admin.email}`)
            .get()
            .then(querySnapshot => {
                querySnapshot.forEach(doc => {
                    console.log(doc.data())
                    const obj = {};
                    obj.email = doc.data().email;
                    obj.name = doc.data().name;
                    obj.Date = doc.data().Date;
                    obj.Time = doc.data().Time;
                    obj.Hotel = doc.data().Hotel;
                    obj.Numberofpeople = doc.data().Numberofpeople;

                    temp.push(obj);
                })
            })
    })
    console.log(temp)
    console.log(temp.length)

    console.log(reservations)

    return (
        <div>

            {/* <div>
                Home
            </div>
            <button onClick={() => Firebase.auth().signOut().then(console.log("signedout"), window.location.href = "/").catch(err => { console.log(err) })}>logout</button> */}
            <React.Fragment>
                <CssBaseline />
                <center>
                    <h1 style={{ margin: "3%", marginBottom: "-2%" }}>Welcome, {username}</h1>
                    <h1 style={{ margin: "3%", marginBottom: "-2%" }}>Find all your reservations here</h1>
                </center>
                <main>
                    <Container className={classes.cardGrid} maxwidth="md">
                        <Grid container spacing={4} className="w3-animate-bottom">
                            {reservations.map((car) => (
                                <Card className={classes.card}>
                                    <CardContent className={classes.cardContent}>
                                        <Typography gutterBottom variant="h5" component="h2">
                                            {car.name}
                                        </Typography>
                                        <Divider />
                                        <Typography variant="overline" style={{ color: "grey" }}>
                                            {car.email}
                                        </Typography>
                                        <br></br>
                                        <Typography variant="overline" style={{ color: "grey" }}>
                                            {car.Numberofpeople}
                                        </Typography>
                                        <br></br>
                                        <Typography variant="overline" style={{ color: "grey" }}>
                                            {car.Hotel}
                                        </Typography>
                                        <br></br>
                                        <Typography variant="overline" style={{ color: "grey" }}>
                                            {car.Time}
                                        </Typography>
                                        <br></br>
                                        <Typography variant="overline" style={{ color: "grey" }}>
                                            {car.Date}
                                        </Typography>
                                    </CardContent>
                                    <CardActions>
                                        <Button
                                            size="small"
                                            color="primary"
                                            onClick={() => {
                                                // setPlaceLogs("192.168.0.2");
                                            }}
                                        >
                                            Delete
                                        </Button>
                                    </CardActions>
                                </Card>
                            ))}
                        </Grid>
                    </Container>
                </main>

            </React.Fragment>


        </div>
    );
}

export default Home;
