import React, { Fragment, useState, useContext, useEffect } from 'react';

// Package Imports
import { Link, Redirect } from 'react-router-dom';
import axios from 'axios';

// Icon & Placeholders Imports
import Reception1 from '../placeholders/reception1.jpg';
import BackIcon from '../placeholders/BackIcon.png';

// Material-UI Imports
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Hidden from '@material-ui/core/Hidden';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import { Firebase, firedb } from '../firebase'
import MuiAlert from "@material-ui/lab/Alert";
import Snackbar from "@material-ui/core/Snackbar";
import { AuthContext } from '../auth/AuthContext'

import { Tab } from 'semantic-ui-react'
import Home from './Home';

// Styling Landing Page


function Alert(props) {
    return <MuiAlert elevation={6} variant="filled" {...props} />;
}

var message = "";
var severity = "success";



const useStyles = makeStyles((theme) => ({
    root: {
        height: '100vh',
    },
    image: {
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundSize: 'cover',
    },
    paper: {
        marginTop: theme.spacing(15),
        [theme.breakpoints.down('md')]: {
            marginTop: theme.spacing(10),
        },
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    heading: {
        paddingTop: theme.spacing(3),
        // paddingBottom: theme.spacing(5),
    },
    buttons: {
        width: '400px',
    },
    backButton: {
        marginTop: theme.spacing(3),
    },
    submitButton: {
        marginTop: theme.spacing(3),
    },
    textFields: {
        marginBottom: theme.spacing(3),
    },
    icons: {
        color: '#fff',
    },
}));

export const Landing = () => {
    const classes = useStyles();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [user, setUser] = useState(null);
    const [open, setOpen] = useState(false);
    const admin = useContext(AuthContext.Consumer)

    console.log(admin)
    const [loading, setLoading] = useState(admin.loading)


    const [username, setUsername] = useState("");
    const [password2, setPassword2] = useState("");
    const [contactNumber, setContactNumber] = useState("");

    const handleClose = (event, reason) => {
        if (reason === "clickaway") {
            return;
        }
        setOpen(false);
    };

    useEffect(() => {
        setLoading(false)
    })

    const signup = () => {
        const details = {
            email: email,
            username: username,
            password1: password,
            password2: password2,
            ph_number: contactNumber,
        };
        console.log(JSON.stringify(details));
        Firebase
            .auth()
            .createUserWithEmailAndPassword(email, password)
            .then((user) => {
                if (user) {
                    console.log(JSON.stringify(user));
                    message = "Account successfully created";
                    severity = "success";
                    firedb.collection("users")
                        .add(details)
                        .then((doc) => {
                            console.log(doc.id);
                            setOpen(true);
                            setTimeout(() => {
                                setUser(user);
                            }, 2000);
                        })
                        .catch((err) =>
                            console.log("error adding user to firebase: ", err)
                        );
                }
            })
            .catch((err) => {
                console.log(err);
                message = err.message;
                severity = "error";
                setOpen(true);
            });
    };




    const login = (e) => {
        e.preventDefault();

