import React, { useState, useCallback, useContext } from "react";
import { withRouter, Redirect } from "react-router";
import { Firebase } from "../firebase";
import { AuthContext } from "../auth/AuthContext";
import { Form, Button, Container, Row, Col, ButtonGroup, ToggleButton } from 'react-bootstrap'

var name
var number
var email
var password
var password2

const SignUp = ({ history }) => {
    const [loggedin, setLoggedin] = useState(false)
    // const [email, setEmail] = useState('')
    // const [password, setPassword] = useState('')



    const handleSignUp = useCallback(async event => {
        console.log(email)
        console.log(password)
        event.preventDefault();
        try {
            await Firebase
                .auth()
                .createUserWithEmailAndPassword(email, password)
                .then(response => {

                    console.log(response)
                    console.log("ook")

                    return (<Redirect to="/home" />)
                })
            history.push("/home");
        } catch (error) {
            alert(error);
        }
    }, [history]);




    return (
        <div>
            <Form.Group controlId="formBasicName" style={{ marginTop: "3%" }}>
                <Form.Label style={{ fontSize: "25px"}}>Name</Form.Label>
                <Form.Control type="email" placeholder="Enter name" value={name} onChange={(event) => {
                    name = event.target.value
                }} />

            </Form.Group>
            <Form.Group controlId="formBasicNumber">
                <Form.Label style={{ fontSize: "25px"}}>Phone Number</Form.Label>
                <Form.Control type="email" placeholder="Enter number" value={number} onChange={(event) => {
                    number = event.target.value
                }} />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>
            <Form.Group controlId="formBasicEmail">
                <Form.Label style={{ fontSize: "25px"}}>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" value={email} onChange={(event) => {
                    email = event.target.value
                }} />
                <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
                <Form.Label style={{ fontSize: "25px"}}>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" value={password} onChange={(event) => {
                    password = event.target.value
                }} />
            </Form.Group>
            <Form.Group controlId="formBasicPassword">
                <Form.Label style={{ fontSize: "25px"}}>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" value={password2} onChange={(event) => {
                    password2 = event.target.value
                }} />
            </Form.Group>
            {password != password2 ? <Button variant="primary" type="submit" disabled size="lg" onClick={handleSignUp} block>
                Sign Up
            </Button> : <Button variant="primary" type="submit" size="lg" onClick={handleSignUp} block>
                Sign Up
            </Button>}
        </div>
    );
};

export default withRouter(SignUp);
