//main page after login
import React, { useContext, useEffect, useState } from 'react';
import { Firebase, firedb } from '../firebase'
import { AuthContext } from '../auth/AuthContext'
import { Redirect } from 'react-router-dom';

//material-ui imports
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";



function Home(props) {
    const admin = useContext(AuthContext);
    const [username, setUsername] = useState('')
    const [phone, setPhone] = useState('')
    const [email, setEmail] = useState('')

    console.log(admin.admin.email)
    console.log(admin.admin.providerData)
    console.log(admin.admin.uid)

    useEffect(() => {
        firedb
            .collection("users")
            .where("email", '==', `${admin.admin.email}`)
            .get()
            .then(querySnapshot => {
                querySnapshot.forEach(doc => {
                    console.log(doc.data())

                })
            })
    })


    return (
        <div>

            {/* <div>
                Home
            </div>
            <button onClick={() => Firebase.auth().signOut().then(console.log("signedout"), window.location.href = "/").catch(err => { console.log(err) })}>logout</button> */}
            <React.Fragment>
                <CssBaseline />
                <h1 style={{ margin: "3%", marginBottom: "-2%" }}>Find all your reservations here</h1>
                <main>
                    <Container className={classes.cardGrid} maxwidth="md">
                        <Grid container spacing={4} className="w3-animate-bottom">
                            {places.map((card) => (
                                <Grid item key={card} xs={12} sm={6} md={4}>
                                    <Grid item xs={12} sm={6} md={4}>

                                        <Card className={classes.card}>
                                            <CardContent className={classes.cardContent}>
                                                <Typography gutterBottom variant="h5" component="h2">
                                                    Lab1
                                        </Typography>
                                                <Divider />
                                                <Typography variant="overline" style={{ color: "grey" }}>
                                                    #Lab
                                        </Typography>
                                                <br />
                                                <Typography variant="overline" style={{ color: "grey" }}>
                                                    192.168.0.2
                                        </Typography>
                                            </CardContent>
                                            <CardActions>
                                                <Button
                                                    size="small"
                                                    color="primary"
                                                    onClick={() => {
                                                        setPlaceLogs("192.168.0.2");
                                                    }}
                                                >
                                                    View
                                        </Button>
                                                <Typography>Status:</Typography>
                                                <span
                                                    className={
                                                        // card.Status === "Green"
                                                        //   ? classes.greenDot
                                                        //   : card.Status === "Red"
                                                        //   ? classes.redDot
                                                        // : classes.yellowDot
                                                        classes.yellowDot
                                                    }
                                                ></span>
                                            </CardActions>
                                        </Card>
                                    </Grid>
                                </Grid>

                            ))}
                        </Grid>
                    </Container>
                </main>
            </React.Fragment>


        </div>
    );
}

export default Home;
