import React, { Component, useCallback } from 'react';
import { Redirect, withRouter } from 'react-router-dom'
import { Firebase } from '../firebase'

import Login from './login'
import Signup from './signup'

//bootstrap imports
import { Form, Button, Container, Row, Col, ButtonGroup, ToggleButton } from 'react-bootstrap'

const radios = [
    { name: 'Yes', value: 'Yes' },
    { name: 'No', value: 'No' },
];

class LoginSignup extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            newvisitor: 'No'
        }

        console.log(this.state.newvisitor)
    }


    componentDidMount() {

    }






    render() {
        console.log(this.state.newvisitor)
        return (
            <div>
                <Container fluid="xl">
                    <Row>
                        <Col>
                            <div style={{ width: "50%", marginTop: "15%", marginLeft: "25%" }}>
                                <center>
                                    <h1 style={{ marginBottom: "5%" }}>Hello, Please login or sign up to continue.</h1>
                                </center>
                                <Form>
                                    <Form.Label>New here?</Form.Label>
                                    <br />
                                    <ButtonGroup toggle>
                                        {radios.map((radio, idx) => (
                                            <ToggleButton
                                                key={idx}
                                                type="radio"
                                                variant="outline-dark"
                                                name="radio"
                                                value={radio.value}
                                                checked={this.state.newvisitor === radio.value}
                                                onChange={(e) => this.setState({ newvisitor: e.currentTarget.value })}
                                            >
                                                {radio.name}
                                            </ToggleButton>
                                        ))}
                                    </ButtonGroup>

                                    {this.state.newvisitor === "No" ?

                                        <Login />

                                        :

                                        <Signup />
                                    }


                                    <div style={{ margin: "3%" }}>
                                        <center>
                                            <span>OR</span>
                                        </center>
                                    </div>

                                </Form>
                            </div>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default LoginSignup;
